import React, { useState } from "react";

const Task = () => {
  const [data, setData] = useState("");
  const [addClickDta, setAddClickData] = useState();
  const getData = (event) => {
    setData(event.target.value);
  };
  const addClickData = () => {
    setAddClickData((oldData) => {
      const allData = {
        id: new Date().getMilliseconds().toString(),
        name: data,
      };
      return [...oldData, allData];
    });
  };
  return (
    <>
      <div className="main_div">
        <div className="center_div">
          <br />
          <h1>TODo List</h1>
          <br />
          <input
            type="text"
            placeholder="Enter Your task"
            onChange={getData}
            value={data}
          />
          <button onClick={addClickData}>+</button>
          <ol>
            {addClickDta.map((currData) => {
              return <li>{currData.name}</li>;
            })}
          </ol>
        </div>
      </div>
    </>
  );
};

export default Task;
