import React, { useState, useEffect } from "react";

const getLocalStorage = () => {
  let list = localStorage.getItem("lists");
  // console.log(list);
  if (list) {
    return JSON.parse(localStorage.getItem("lists"));
  } else {
    return [];
  }
};

const TODO_List = () => {
  const [setitem, setNewItem] = useState("");
  const [newdata, newSetData] = useState(getLocalStorage());

  const itemEvent = (event) => {
    setNewItem(event.target.value);
  };
  const clickItem = () => {
    newSetData((oldData) => {
      const allData = { id: new Date().getTime.toString(), name: setitem };
      return [...oldData, allData];
    });
    setNewItem("");
  };

  const deleteItem = (index) => {
    newSetData((oldData) => {
      return oldData.filter((arr, num) => {
        return index !== num;
      });
    });
  };

  useEffect(() => {
    localStorage.setItem("lists", JSON.stringify(newdata));
  }, [newdata]);

  return (
    <>
      <div className="main_div">
        <div className="center_div">
          <br />
          <h1>TODo List</h1>
          <br />
          <input
            type="text"
            placeholder="Enter Your Name"
            onChange={itemEvent}
            value={setitem}
          ></input>
          <button onClick={clickItem}>+</button>
          <ol>
            {newdata.map((val) => {
              return (
                <div className="todo_style" key={val.id} id={val.id}>
                  <i
                    className="fa fa-times"
                    aria-hidden="true"
                    onClick={() => deleteItem(val.id)}
                  ></i>
                  <li>{val}</li>
                </div>
              );
            })}
          </ol>
        </div>
      </div>
    </>
  );
};

export default TODO_List;
